import React from 'react';

function Form () {
    const [data, setData] = React.useState({
        firstName: "",
        lastName: "",
        email: "",
        meetInPerson: true,
        genres: [],
        suggestedBook: "",
        comments: ""
    })

    function handleChange(event) {
        const [name, type, checked, value] = event.target;
        setData(prevData => {
            return {
                ...prevData,
                [name]: type === "checkbox" ? checked : value
            }
        })
    }

    return (
        <div className="container">
            <form>
                <label htmlFor="firstName">First Name: </label>
                <input
                    name="firstName"
                    value={data.firstName}
                    onChange={handleChange}
                    type="text"
                />
                <label htmlFor="">Last Name: </label>
                <input
                    type="text"
                    name="lastName"
                    value={data.lastName}
                    onChange={handleChange}
                />
                <label htmlFor="email">Email: </label>
                <input
                    type="text"
                    name="email"
                    onChange={handleChange}
                    value={data.email}
                />
                <label htmlFor="meetInPerson">Would you like to meet in person?</label>
                <input 
                    type="checkbox"
                    name="meetInPerson"
                    checked={data.meetInPerson}
                    onChange={handleChange}
                />
            </form>
        </div>
    );
}

export default Form
