import React from 'react';
// import GenreOptions from './Select';

function Form () {

    const [data, setData] = React.useState(
        {
            firstName: "",
            lastName: "",
            email: "",
            meetInPerson: false,
            zip: "",
            radius: "",
            genres: [],
            leadClub: false,
            comments: ""
        })

        // const options = [
        //     {value: "fiction", label: "Fiction", isPicked: false},
        //     {value: "non-fiction", label: "Non-fiction", isPicked: false},
        //     {value: "mystery", label: "Mystery", isPicked: false}
        // ]

    function handleChange(event) {
        const {name, type, checked, value} = event.target;
        setData(prevData => {
            return {
                ...prevData,
                [name]: type === "checkbox" ? checked : value,
            }
        })
    }

    function handleRadio(event) {
        const {name, value} = event.target;
        const isChecked = value === 'true' ? true : false;
        setData(prevData => {
            return {
                ...prevData,
                [name]: isChecked
            }
        })
    }
    function handleGenres(event) {
        const {name, value} = event.target;
        const newArr = data.genres;
        const isInArray = (value) => newArr.filter(v => value === v).length > 0;
        console.log(isInArray(value))
        setData(prevData => {
            return {
                ...prevData,
                [name]: isInArray ? console.log('error') : newArr.push(value)
            }
        })

    }
    console.log(data.genres)
    console.log(data)

    function handleSubmit (event) {
        event.preventDefault()
        console.log('submit')
    }

    return (
        <div className="container">
            <h1>The Book Club Finder</h1>
            <h2>Please fill out our form and we'll match you with the best group</h2>
            <h3>Personal Information</h3>
            <form onSubmit={handleSubmit}>
                <div className="personal-info">
                    <div className="first-name">
                        <label htmlFor="firstName">First Name <span className="red">*</span></label>
                        <input
                            type="text"
                            name="firstName"
                            required
                            value={data.firstName}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="last-name">
                        <label htmlFor="">Last Name <span className="red">*</span></label>
                        <input
                            type="text"
                            name="lastName"
                            required
                            value={data.lastName}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="email">
                        <label htmlFor="email">Email <span className="red">*</span></label>
                        <input
                            type="email"
                            name="email"
                            required
                            onChange={handleChange}
                            value={data.email}
                        />
                    </div>
                </div>
                <h3>Let's Talk Books</h3>
                <div className="book-info">
                    <div>
                        <fieldset className="meet-in-person">
                            <legend htmlFor="meetInPerson">Would you like to meet in person?</legend>
                            <div className="yes-input">
                                <input
                                    value="true"
                                    type="radio"
                                    name="meetInPerson"
                                    onChange={handleRadio}
                                    checked={data.meetInPerson.value}
                                /> Yes
                            </div>
                            <div className="no-input">
                                <input 
                                    type="radio"
                                    name="meetInPerson"
                                    value="false"
                                    onChange={handleRadio}
                                    checked={data.meetInPerson.value}
                                /> No
                            </div>
                        </fieldset>
                    </div>
                    <div className="zipcode">
                        <label htmlFor="zip">Zip code: </label>
                        <input
                            type="number"
                            min="00000"
                            max="99999"
                            name="zip"
                            onChange={handleChange}
                            value={data.zip}
                        />
                    </div>
                    <div className="radius-travel">
                        <label htmlFor="radius">How far are you willing to travel?</label>
                        <select
                            id="radius"
                            value={data.radius}
                            onChange={handleChange}
                            name="radius"
                        >
                            <option value=""></option>
                            <option value="5">up to 5 miles</option>
                            <option value="10">5-10 miles</option>
                            <option value="20">10-20 miles</option>
                            <option value="100">20+ miles</option>
                        </select>
                    </div>
                    <div className="genres">
                        <label htmlFor="genres">What genres are you most interested in?</label>
                        <select
                            id="genres"
                            value={data.genres}
                            onChange={handleGenres}
                            name="genres"
                            multiple={true}
                        >
                            <option value=""></option>
                            <option value="fiction">Fiction</option>
                            <option value="non-fiction">Non-fiction</option>
                            <option value="historical-fiction">Historical fiction</option>
                            <option value="mystery">Mystery</option>
                            <option value="fantasy">Fantasy</option>
                            <option value="educational">Educational</option>
                            <option value="young-adult">Young Adult</option>
