import React from 'react';
// import GenreOptions from './Select';

function Form () {
    const [data, setData] = React.useState(
        {
            firstName: "",
            lastName: "",
            email: "",
            meetInPerson: true,
            genres: "",
            suggestedBook: "",
            comments: ""
        })

    function handleChange(event) {
        const {name, type, checked, value} = event.target;
        setData(prevData => {
            return {
                ...prevData,
                [name]: type === "checkbox" ? checked : value

            }
        })
    }

    console.log(data)

    return (
        <div className="container">
            <form>
                <label htmlFor="firstName">First Name: </label>
                <input
                    name="firstName"
                    value={data.firstName}
                    onChange={handleChange}
                    type="text"
                />
                <label htmlFor="">Last Name: </label>
                <input
                    type="text"
                    name="lastName"
                    value={data.lastName}
                    onChange={handleChange}
                />
                <label htmlFor="email">Email: </label>
                <input
                    type="text"
                    name="email"
                    onChange={handleChange}
                    value={data.email}
                />
                <label htmlFor="meetInPerson">Would you like to meet in person?</label>
                <input 
                    type="checkbox"
                    name="meetInPerson"
                    checked={data.meetInPerson}
                    onChange={handleChange}
                />
                <label htmlFor="genres">What genres are you interested in? (Choose multiple)</label>
                <select
                    id="genres"
                    value={data.genres}
                    onChange={handleChange}
                    name="genres"
                >
                    <option values="fiction">Fiction</option>
                    <option values="non-fiction">Non-fiction</option>
                    <option values="historical-fiction">Historical fiction</option>
                    <option values="mystery">Mystery</option>
                    <option values="fantasy">Fantasy</option>
                    <option values="educational">Educational</option>
                    <option values="young-adult">Young Adult</option>
                    <option values="self-help">Self Help</option>
                    <option values="biography">Biography</option>
                    <option values="poetry">Poetry</option>
                    <option values="history">History</option>
                    <option values="horror">Horror</option>
                    <option values="romance">Romance</option>
                    <option values="autobiography">Autobiography</option>
                    <option values="mythology">Mythology</option>
                    <option values="essay">Essay</option>
                    <option values="philosophy">Philosophy</option>
                </select>

                {/* <GenreOptions
                    name="genres"
                    className="basic-multi-select"
                    classNamePrefix="select"
                    onChange={handleChange}

                /> */}
            </form>
        </div>
    );
}

export default Form
